import { useState } from 'react';
import LoginForm from '../components/LoginForm';

export default function Login() {
  const [role, setRole] = useState('');
  return (
    <div className="page-container">
      <div style={{ maxWidth: '400px', margin: '0 auto', textAlign: 'center' }}>
        <h1>Welcome Back</h1>
        <p style={{ color: '#64748b', marginBottom: '2rem' }}>
          Log in to access your account
        </p>
        {!role ? (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem', marginBottom: '2rem' }}>
            <button className="linkedin-btn linkedin-btn-primary" onClick={() => setRole('client')}>Login as Client</button>
            <button className="linkedin-btn linkedin-btn-secondary" onClick={() => setRole('freelancer')}>Login as Freelancer</button>
          </div>
        ) : (
          <>
            <LoginForm role={role} />
            <button style={{ marginTop: 24, background: 'none', color: '#0a66c2', border: 'none', cursor: 'pointer', textDecoration: 'underline' }} onClick={() => setRole('')}>Back</button>
          </>
        )}
      </div>
    </div>
  );
}
