import { useState } from 'react';

export default function JobForm() {
  const [job, setJob] = useState({ title: '', description: '', budget: '', deadline: '' });

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const jobs = JSON.parse(localStorage.getItem('jobs') || '[]');
    const newJob = { ...job, id: Date.now(), offers: [] };
    localStorage.setItem('jobs', JSON.stringify([...jobs, newJob]));
    setJob({ title: '', description: '', budget: '', deadline: '' });
    alert('Job posted!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Title
        <input name="title" value={job.title} onChange={handleChange} required />
      </label>
      <label>Description
        <textarea name="description" value={job.description} onChange={handleChange} required />
      </label>
      <label>Budget
        <input name="budget" type="number" value={job.budget} onChange={handleChange} required />
      </label>
      <label>Deadline
        <input name="deadline" type="date" value={job.deadline} onChange={handleChange} required />
      </label>
      <button type="submit">Post Job</button>
    </form>
  );
}
