import { useState, useEffect, useRef } from 'react';

function fileToBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = () => resolve(reader.result);
    reader.onerror = reject;
    reader.readAsDataURL(file);
  });
}

export default function ProfileForm() {
  const [profile, setProfile] = useState({ profileId: '', userId: '', bio: '', phone: '', skills: '', experience: '', cv: '', cvData: '' });
  const [user, setUser] = useState({ fullName: '', email: '', role: '', password: '' });
  const [cvName, setCvName] = useState('');
  const [editing, setEditing] = useState(false);
  const fileInputRef = useRef();

  useEffect(() => {
    const savedProfile = localStorage.getItem('profile');
    if (savedProfile) {
      const parsed = JSON.parse(savedProfile);
      setProfile(parsed);
      if (parsed.cv) setCvName(parsed.cv);
    }
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setCvName(file.name);
      const base64 = await fileToBase64(file);
      setProfile(prev => ({ ...prev, cv: file.name, cvData: base64 }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem('profile', JSON.stringify(profile));
    setEditing(false);
    alert('Profile saved!');
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 500, margin: '0 auto' }}>
      <h2>Profile</h2>
      <label>Full Name
        <input name="fullName" value={user.fullName} disabled style={{ background: '#f1f5f9' }} />
      </label>
      <label>Email
        <input name="email" value={user.email} disabled style={{ background: '#f1f5f9' }} />
      </label>
      <label>Password
        <input name="password" value={user.password} type="password" disabled style={{ background: '#f1f5f9' }} />
      </label>
      <label>Role
        <input name="role" value={user.role} disabled style={{ background: '#f1f5f9' }} />
      </label>
      <label>Phone
        <input name="phone" value={profile.phone} onChange={handleChange} disabled={!editing} />
      </label>
      <label>Bio
        <textarea name="bio" value={profile.bio} onChange={handleChange} disabled={!editing} />
      </label>
      <label>Skills
        <input name="skills" value={profile.skills} onChange={handleChange} disabled={!editing} />
      </label>
      <label>Experience
        <input name="experience" value={profile.experience} onChange={handleChange} disabled={!editing} />
      </label>
      {user.role === 'freelancer' && (
        <label>CV
          <input type="file" accept=".pdf,.doc,.docx" ref={fileInputRef} onChange={handleFileChange} disabled={!editing} />
          {cvName && profile.cvData && (
            <div style={{ marginTop: 8 }}>
              <a href={profile.cvData} download={cvName} style={{ color: '#2563eb' }}>Download/View CV: {cvName}</a>
            </div>
          )}
        </label>
      )}
      {!editing && (
        <button type="button" className="linkedin-btn linkedin-btn-primary" style={{ marginTop: 16 }} onClick={() => setEditing(true)}>Edit Profile</button>
      )}
      {editing && (
        <button type="submit" className="linkedin-btn linkedin-btn-primary" style={{ marginTop: 16 }}>Save Profile</button>
      )}
    </form>
  );
}
