import { useState, useEffect } from 'react';

function generateJobId() {
  return 'j_' + Date.now() + '_' + Math.floor(Math.random() * 10000);
}

export default function JobForm() {
  const [job, setJob] = useState({ title: '', description: '', budget: '', deadline: '' });
  const [user, setUser] = useState(null);

  useEffect(() => {
    const u = localStorage.getItem('user');
    setUser(u ? JSON.parse(u) : null);
  }, []);

  if (!user || user.role !== 'client') return null;

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const jobs = JSON.parse(localStorage.getItem('jobs') || '[]');
    const jobId = generateJobId();
    const createdAt = new Date().toISOString();
    const newJob = {
      jobId,
      clientId: user.userId,
      title: job.title,
      description: job.description,
      budget: job.budget,
      deadline: job.deadline,
      status: 'open',
      createdAt,
      offers: [],
      postedBy: user.fullName,
    };
    localStorage.setItem('jobs', JSON.stringify([...jobs, newJob]));
    setJob({ title: '', description: '', budget: '', deadline: '' });
    alert('Job posted!');
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '60vh', justifyContent: 'center' }}>
      <h1 style={{ fontWeight: 700, fontSize: '2rem', marginBottom: '2rem' }}>Post a New Job</h1>
      <form onSubmit={handleSubmit} style={{ background: 'white', borderRadius: 12, boxShadow: '0 2px 16px rgba(10,102,194,0.07)', padding: '2.5rem 2rem', minWidth: 320, maxWidth: 400, width: '100%', display: 'flex', flexDirection: 'column', gap: '1.2rem' }}>
        <label style={{ fontWeight: 500 }}>Title
          <input name="title" value={job.title} onChange={handleChange} required style={{ width: '100%', marginTop: 4, padding: '0.5rem', borderRadius: 6, border: '1px solid #e2e8f0' }} />
        </label>
        <label style={{ fontWeight: 500 }}>Description
          <textarea name="description" value={job.description} onChange={handleChange} required style={{ width: '100%', marginTop: 4, padding: '0.5rem', borderRadius: 6, border: '1px solid #e2e8f0', minHeight: 60 }} />
        </label>
        <label style={{ fontWeight: 500 }}>Budget
          <input name="budget" type="number" value={job.budget} onChange={handleChange} required style={{ width: '100%', marginTop: 4, padding: '0.5rem', borderRadius: 6, border: '1px solid #e2e8f0' }} />
        </label>
        <label style={{ fontWeight: 500 }}>Deadline
          <input name="deadline" type="date" value={job.deadline} onChange={handleChange} required style={{ width: '100%', marginTop: 4, padding: '0.5rem', borderRadius: 6, border: '1px solid #e2e8f0' }} />
        </label>
        <button type="submit" className="linkedin-btn linkedin-btn-primary" style={{ marginTop: 12 }}>Post Job</button>
      </form>
    </div>
  );
}
