import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const PRESET_JOBS = [
  {
    id: 1001,
    title: 'Build a React Portfolio Website',
    description: 'Looking for a freelancer to build a modern, responsive portfolio website using React.',
    budget: 500,
    deadline: '2024-07-15',
    postedBy: 'demo-client1',
  },
  {
    id: 1002,
    title: 'Design a Company Logo',
    description: 'Need a creative designer to design a logo for our new startup.',
    budget: 200,
    deadline: '2024-07-10',
    postedBy: 'demo-client2',
  },
  {
    id: 1003,
    title: 'Develop a Mobile App',
    description: 'Seeking a mobile app developer for a cross-platform app (iOS/Android).',
    budget: 1500,
    deadline: '2024-08-01',
    postedBy: 'demo-client3',
  },
];

function ensurePresetJobs() {
  const jobs = JSON.parse(localStorage.getItem('jobs') || '[]');
  if (jobs.length === 0) {
    localStorage.setItem('jobs', JSON.stringify(PRESET_JOBS));
  }
}

export default function Home() {
  const [loggedOut, setLoggedOut] = useState(false);
  const [profile, setProfile] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [liked, setLiked] = useState([]);

  useEffect(() => {
    ensurePresetJobs();
    if (localStorage.getItem('justLoggedOut') === 'true') {
      setLoggedOut(true);
      localStorage.removeItem('justLoggedOut');
    }
    const p = localStorage.getItem('profile');
    setProfile(p ? JSON.parse(p) : null);
    setJobs(JSON.parse(localStorage.getItem('jobs') || '[]'));
    if (p) {
      const user = JSON.parse(p);
      setLiked(JSON.parse(localStorage.getItem(`liked_${user.email}`) || '[]'));
    }
  }, []);

  const handleLike = (jobId) => {
    if (!profile) return;
    let newLiked = liked.includes(jobId)
      ? liked.filter(id => id !== jobId)
      : [...liked, jobId];
    setLiked(newLiked);
    localStorage.setItem(`liked_${profile.email}`, JSON.stringify(newLiked));
  };

  const handleApply = (jobId) => {
    alert('Application submitted! (Demo only)');
  };

  // Show hero if not logged in
  if (!profile) {
    return (
      <div className="linkedin-home-bg">
        {loggedOut && (
          <div style={{ background: '#d1fae5', color: '#065f46', padding: '1rem', borderRadius: 8, marginBottom: '1.5rem', textAlign: 'center' }}>
            Logged out
          </div>
        )}
        <div className="linkedin-hero-container">
          <div className="linkedin-hero-left">
            <h1 className="linkedin-hero-title">Welcome to FreelanceHub</h1>
            <p className="linkedin-hero-desc">
              Connect with top freelancers and clients. Post jobs, find work, and grow your career or business—all in one place.
            </p>
            <div className="linkedin-hero-cta">
              <Link to="/register" className="linkedin-btn linkedin-btn-primary">Join Now</Link>
              <Link to="/login" className="linkedin-btn linkedin-btn-secondary">Sign In</Link>
            </div>
          </div>
          <div className="linkedin-hero-right">
            <img src="/freelancer.webp" alt="FreelanceHub illustration" className="linkedin-hero-img" />
          </div>
        </div>
      </div>
    );
  }

  // Show job feed for logged-in users
  return (
    <div className="page-container" style={{ background: 'linear-gradient(120deg, #f3f6fb 0%, #e9ecef 100%)', minHeight: '100vh' }}>
      <h1 style={{ color: '#0a66c2', fontWeight: 700, marginBottom: '2rem' }}>Job Feed</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem' }}>
        {jobs.map(job => (
          <div key={job.id} style={{ background: 'white', borderRadius: 12, boxShadow: '0 2px 8px rgba(10,102,194,0.07)', padding: '2rem', minWidth: 320, maxWidth: 400, flex: '1 1 320px', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
            <div>
              <h2 style={{ color: '#0a66c2', margin: 0 }}>{job.title}</h2>
              <p style={{ color: '#434649', margin: '1rem 0' }}>{job.description}</p>
              <p style={{ margin: 0 }}><b>Budget:</b> ${job.budget} &nbsp; <b>Deadline:</b> {job.deadline}</p>
            </div>
            <div style={{ marginTop: '1.5rem', display: 'flex', gap: '1rem', alignItems: 'center' }}>
              <button onClick={() => handleLike(job.id)} className={liked.includes(job.id) ? 'linkedin-btn linkedin-btn-primary' : 'linkedin-btn linkedin-btn-secondary'}>
                {liked.includes(job.id) ? 'Liked' : 'Like'}
              </button>
              {profile.role === 'freelancer' && (
                <button onClick={() => handleApply(job.id)} className="linkedin-btn linkedin-btn-primary">Apply</button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
