import { useState } from 'react';

function generateId() {
  return 'u_' + Date.now() + '_' + Math.floor(Math.random() * 10000);
}

export default function RegisterForm() {
  const [form, setForm] = useState({ fullName: '', email: '', password: '', role: 'client' });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userId = generateId();
    const createdAt = new Date().toISOString();
    const user = { ...form, userId, createdAt };
    localStorage.setItem('user', JSON.stringify(user));
    localStorage.setItem('loggedIn', 'true');
    // Create profile
    const profileId = 'p_' + Date.now() + '_' + Math.floor(Math.random() * 10000);
    const profile = {
      profileId,
      userId,
      bio: '',
      phone: '',
      skills: '',
      experience: '',
    };
    localStorage.setItem('profile', JSON.stringify(profile));
    alert('User registered!');
    window.location.href = '/';
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Full Name
        <input name="fullName" placeholder="Full Name" onChange={handleChange} required />
      </label>
      <label>Email
        <input name="email" placeholder="Email" onChange={handleChange} required />
      </label>
      <label>Password
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required />
      </label>
      <label>Register as
        <select name="role" value={form.role} onChange={handleChange}>
          <option value="client">Client</option>
          <option value="freelancer">Freelancer</option>
        </select>
      </label>
      <button type="submit">Register</button>
    </form>
  );
}
