import { useState, useEffect, useRef } from 'react';

function fileToBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = () => resolve(reader.result);
    reader.onerror = reject;
    reader.readAsDataURL(file);
  });
}

export default function ProfileForm() {
  const [profile, setProfile] = useState({ name: '', email: '', role: 'client', cv: '', cvData: '', logo: '', logoData: '' });
  const [cvName, setCvName] = useState('');
  const [logoName, setLogoName] = useState('');
  const fileInputRef = useRef();
  const logoInputRef = useRef();

  useEffect(() => {
    const saved = localStorage.getItem('profile');
    if (saved) {
      const parsed = JSON.parse(saved);
      setProfile(parsed);
      if (parsed.cv) setCvName(parsed.cv);
      if (parsed.logo) setLogoName(parsed.logo);
    }
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setCvName(file.name);
      const base64 = await fileToBase64(file);
      setProfile(prev => ({ ...prev, cv: file.name, cvData: base64 }));
    }
  };

  const handleLogoChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setLogoName(file.name);
      const base64 = await fileToBase64(file);
      setProfile(prev => ({ ...prev, logo: file.name, logoData: base64 }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem('profile', JSON.stringify(profile));
    alert('Profile saved!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name
        <input name="name" value={profile.name} onChange={handleChange} required />
      </label>
      <label>Email
        <input name="email" type="email" value={profile.email} onChange={handleChange} required />
      </label>
      <label>Role
        <input name="role" value={profile.role} disabled style={{ background: '#f1f5f9' }} />
      </label>
      {profile.role === 'freelancer' && (
        <label>CV
          <input type="file" accept=".pdf,.doc,.docx" ref={fileInputRef} onChange={handleFileChange} />
          {cvName && profile.cvData && (
            <div style={{ marginTop: 8 }}>
              <a href={profile.cvData} download={cvName} style={{ color: '#2563eb' }}>Download/View CV: {cvName}</a>
            </div>
          )}
        </label>
      )}
      {profile.role === 'client' && (
        <label>Company Logo
          <input type="file" accept="image/*" ref={logoInputRef} onChange={handleLogoChange} />
          {logoName && profile.logoData && (
            <div style={{ marginTop: 8 }}>
              <img src={profile.logoData} alt="Company Logo" style={{ maxWidth: 120, maxHeight: 120, borderRadius: 8, border: '1px solid #e2e8f0' }} />
              <div style={{ color: '#2563eb' }}>{logoName}</div>
            </div>
          )}
        </label>
      )}
      <button type="submit">Save Profile</button>
    </form>
  );
}
