import { useState } from 'react';
import ClientRegisterForm from '../components/ClientRegisterForm';
import FreelancerRegisterForm from '../components/FreelancerRegisterForm';

export default function Register() {
  const [role, setRole] = useState('');

  if (!role) {
    return (
      <div className="page-container" style={{ textAlign: 'center', marginTop: '3rem' }}>
        <h1>Register</h1>
        <p>Choose your role to get started:</p>
        <button className="linkedin-btn linkedin-btn-primary" style={{ marginRight: 16 }} onClick={() => setRole('client')}>Register as Client</button>
        <button className="linkedin-btn linkedin-btn-secondary" onClick={() => setRole('freelancer')}>Register as Freelancer</button>
      </div>
    );
  }

  return (
    <div className="page-container">
      {role === 'client' && <ClientRegisterForm />}
      {role === 'freelancer' && <FreelancerRegisterForm />}
      <button className="linkedin-btn linkedin-btn-secondary" style={{ marginTop: 24 }} onClick={() => setRole('')}>Back</button>
    </div>
  );
}
