import { Link, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

export default function Navbar() {
  const [profile, setProfile] = useState(null);
  const [loggedIn, setLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    setLoggedIn(localStorage.getItem('loggedIn') === 'true');
    const p = localStorage.getItem('profile');
    setProfile(p ? JSON.parse(p) : null);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('loggedIn');
    localStorage.removeItem('profile');
    navigate('/login');
  };

  return (
    <nav>
      <ul>
        <li>
          <Link to="/" style={{ fontSize: '1.25rem', fontWeight: 'bold' }}>
            FreelanceHub
          </Link>
        </li>
        <li style={{ marginLeft: 'auto' }}>
          <Link to="/">Home</Link>
        </li>
        {loggedIn && profile?.role === 'freelancer' && (
          <li>
            <Link to="/jobs">Jobs</Link>
          </li>
        )}
        {loggedIn && profile?.role === 'client' && (
          <>
            <li>
              <Link to="/post-job">Post Job</Link>
            </li>
            <li>
              <Link to="/dashboard">Dashboard</Link>
            </li>
          </>
        )}
        {loggedIn && (
          <li>
            <Link to="/profile">Profile</Link>
          </li>
        )}
        {!loggedIn && (
          <>
            <li>
              <Link to="/register">Register</Link>
            </li>
            <li>
              <Link to="/login" style={{ backgroundColor: 'var(--primary-color)', color: 'white', padding: '0.5rem 1rem', borderRadius: '6px' }}>
                Login
              </Link>
            </li>
          </>
        )}
        {loggedIn && (
          <li>
            <button onClick={handleLogout} style={{ background: 'none', border: 'none', color: 'var(--primary-color)', cursor: 'pointer', fontWeight: 500 }}>
              Logout
            </button>
          </li>
        )}
      </ul>
    </nav>
  );
}
