import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export default function JobList() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    setJobs(JSON.parse(localStorage.getItem('jobs') || '[]'));
  }, []);

  if (!jobs.length) return <p>No jobs posted yet.</p>;

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
      {jobs.map(job => (
        <div key={job.id} style={{ background: 'white', padding: '1.5rem', borderRadius: 8, boxShadow: '0 1px 3px rgba(0,0,0,0.07)' }}>
          <h2 style={{ margin: 0 }}>{job.title}</h2>
          <p>{job.description}</p>
          <p><b>Budget:</b> ${job.budget} &nbsp; <b>Deadline:</b> {job.deadline}</p>
          <Link to={`/jobs/${job.id}`}>View Details</Link>
        </div>
      ))}
    </div>
  );
}
