import { Link, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

export default function Navbar() {
  const [user, setUser] = useState(null);
  const [loggedIn, setLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    setLoggedIn(localStorage.getItem('loggedIn') === 'true');
    const u = localStorage.getItem('user');
    setUser(u ? JSON.parse(u) : null);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('loggedIn');
    localStorage.removeItem('user');
    localStorage.removeItem('profile');
    navigate('/');
  };

  return (
    <nav>
      <ul>
        <li>
          <Link to="/" style={{ fontSize: '1.25rem', fontWeight: 'bold' }}>
            FreelanceHub
          </Link>
        </li>
        <li style={{ marginLeft: 'auto' }}>
          <Link to="/">Home</Link>
        </li>
        {loggedIn && <li><Link to="/feed">Feed</Link></li>}
        {loggedIn && user?.role === 'client' && (
          <>
            <li><Link to="/post-job">Post Job</Link></li>
            <li><Link to="/dashboard">Dashboard</Link></li>
            <li><Link to="/applications">Applications</Link></li>
          </>
        )}
        {loggedIn && user?.role === 'freelancer' && (
          <li><Link to="/my-applications">My Applications</Link></li>
        )}
        {loggedIn && (
          <li><Link to="/profile">Profile</Link></li>
        )}
        {!loggedIn && (
          <>
            <li><Link to="/register">Register</Link></li>
            <li><Link to="/login" className="linkedin-btn linkedin-btn-secondary">Login</Link></li>
          </>
        )}
        {loggedIn && (
          <li>
            <button onClick={handleLogout} style={{ background: 'none', border: 'none', color: 'var(--primary-color)', cursor: 'pointer', fontWeight: 500, transition: 'color 0.2s' }} onMouseOver={e => e.target.style.color='#004182'} onMouseOut={e => e.target.style.color='var(--primary-color)'}>
              Logout
            </button>
          </li>
        )}
      </ul>
    </nav>
  );
}
