import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';

function OfferForm({ jobId, onOffer }) {
  const [amount, setAmount] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!amount) return;
    onOffer({ amount, message, status: 'pending', id: Date.now() });
    setAmount('');
    setMessage('');
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: '2rem' }}>
      <label>Offer Amount
        <input type="number" value={amount} onChange={e => setAmount(e.target.value)} required />
      </label>
      <label>Message
        <textarea value={message} onChange={e => setMessage(e.target.value)} />
      </label>
      <button type="submit">Submit Offer</button>
    </form>
  );
}

function OfferList({ offers }) {
  if (!offers?.length) return <p>No offers yet.</p>;
  return (
    <div style={{ marginTop: '2rem' }}>
      <h3>Offers</h3>
      <ul>
        {offers.map(offer => (
          <li key={offer.id}>
            <b>${offer.amount}</b> - {offer.message} <span style={{ color: '#64748b' }}>({offer.status})</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default function JobDetails() {
  const { id } = useParams();
  const [job, setJob] = useState(null);

  useEffect(() => {
    const jobs = JSON.parse(localStorage.getItem('jobs') || '[]');
    setJob(jobs.find(j => String(j.id) === String(id)));
  }, [id]);

  const handleOffer = (offer) => {
    const jobs = JSON.parse(localStorage.getItem('jobs') || '[]');
    const idx = jobs.findIndex(j => String(j.id) === String(id));
    if (idx !== -1) {
      jobs[idx].offers = jobs[idx].offers || [];
      jobs[idx].offers.push(offer);
      localStorage.setItem('jobs', JSON.stringify(jobs));
      setJob({ ...jobs[idx] });
    }
  };

  if (!job) return <p>Loading...</p>;

  return (
    <div className="page-container">
      <h1>{job.title}</h1>
      <p>{job.description}</p>
      <p><b>Budget:</b> ${job.budget} &nbsp; <b>Deadline:</b> {job.deadline}</p>
      <OfferForm jobId={job.id} onOffer={handleOffer} />
      <OfferList offers={job.offers} />
    </div>
  );
}
