import { useState, useEffect, useRef } from 'react';

export default function ProfileForm() {
  const [profile, setProfile] = useState({ name: '', email: '', role: 'client', cv: '' });
  const [cvName, setCvName] = useState('');
  const fileInputRef = useRef();

  useEffect(() => {
    const saved = localStorage.getItem('profile');
    if (saved) {
      const parsed = JSON.parse(saved);
      setProfile(parsed);
      if (parsed.cv) setCvName(parsed.cv);
    }
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setCvName(file.name);
      // For demo, just save the file name in localStorage
      setProfile(prev => ({ ...prev, cv: file.name }));
      // In a real app, you'd upload the file to a server here
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem('profile', JSON.stringify(profile));
    alert('Profile saved!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name
        <input name="name" value={profile.name} onChange={handleChange} required />
      </label>
      <label>Email
        <input name="email" type="email" value={profile.email} onChange={handleChange} required />
      </label>
      <label>Role
        <input name="role" value={profile.role} disabled style={{ background: '#f1f5f9' }} />
      </label>
      {profile.role === 'freelancer' && (
        <label>CV
          <input type="file" accept=".pdf,.doc,.docx" ref={fileInputRef} onChange={handleFileChange} />
          {cvName && (
            <div style={{ marginTop: 8 }}>
              <span style={{ color: '#2563eb' }}>Uploaded: {cvName}</span>
            </div>
          )}
        </label>
      )}
      <button type="submit">Save Profile</button>
    </form>
  );
}
