import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

function generateOfferId() {
  return 'o_' + Date.now() + '_' + Math.floor(Math.random() * 10000);
}

export default function ApplyJob() {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [job, setJob] = useState(undefined);
  const [user, setUser] = useState(undefined);
  const [proposalText, setProposalText] = useState('');
  const [amount, setAmount] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const jobs = JSON.parse(localStorage.getItem('jobs') || '[]');
    setJob(jobs.find(j => j.jobId === jobId) || null);
    const u = localStorage.getItem('user');
    setUser(u ? JSON.parse(u) : null);
    setLoading(false);
  }, [jobId]);

  if (loading) return <p>Loading...</p>;
  if (!user) return <p>You must be logged in as a freelancer to apply for a job.</p>;
  if (user.role !== 'freelancer') return <p>Only freelancers can apply for jobs.</p>;
  if (!job) return <p>Job not found.</p>;

  const handleSubmit = (e) => {
    e.preventDefault();
    const jobsArr = JSON.parse(localStorage.getItem('jobs') || '[]');
    const idx = jobsArr.findIndex(j => j.jobId === jobId);
    if (idx !== -1) {
      jobsArr[idx].offers = jobsArr[idx].offers || [];
      if (!jobsArr[idx].offers.some(o => o.freeLancerId === user.userId)) {
        jobsArr[idx].offers.push({
          offerId: generateOfferId(),
          jobId,
          freeLancerId: user.userId,
          amount,
          proposalText,
          status: 'pending',
          createdAt: new Date().toISOString(),
        });
        localStorage.setItem('jobs', JSON.stringify(jobsArr));
        alert('Application submitted!');
        navigate('/jobs');
      } else {
        alert('You have already applied to this job.');
        navigate('/jobs');
      }
    }
  };

  return (
    <div className="page-container">
      <h1>Apply to: {job.title}</h1>
      <p>{job.description}</p>
      <p><b>Budget:</b> ${job.budget} &nbsp; <b>Deadline:</b> {job.deadline}</p>
      <form onSubmit={handleSubmit} style={{ marginTop: 24, maxWidth: 500 }}>
        <label style={{ display: 'block', marginBottom: 8 }}>Cover Letter / Message
          <textarea value={proposalText} onChange={e => setProposalText(e.target.value)} required style={{ width: '100%', minHeight: 60, marginTop: 4 }} />
        </label>
        <label style={{ display: 'block', marginBottom: 16 }}>Expected Rate ($)
          <input type="number" value={amount} onChange={e => setAmount(e.target.value)} required style={{ width: '100%', marginTop: 4 }} />
        </label>
        <button type="submit" className="linkedin-btn linkedin-btn-primary">Submit Application</button>
        <button type="button" onClick={() => navigate('/jobs')} className="linkedin-btn linkedin-btn-secondary" style={{ marginLeft: 8 }}>Cancel</button>
      </form>
    </div>
  );
}
